import React, { useState, useContext } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import UserViewBuyer from './UserViewBuyer';
import MyContext from "../my_context";

const LoginView = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { artistsInfo, setArtistsInfo ,  isLoggedIn, setIsLoggedIn, user, setUser } = useContext(MyContext);

    // Agregar aquí la lógica para manejar el envío del formulario
    const handleSubmit = (e) => {
        e.preventDefault();
        // Lógica de inicio de sesión
        console.log('Email:', email);
        console.log('Password:', password);

        // Verificar las credenciales ingresadas con el JSON de artistas
        const foundUser = artistsInfo.find((artist) => artist.email === email && artist.password === password);

        if (foundUser) {
            setIsLoggedIn(true);
            setUser(foundUser);

            const artist_index = artistsInfo.findIndex((element) => element.user_id = user.user_id);
            artistsInfo[artist_index].favorites = [];
            setArtistsInfo([...artistsInfo]);


        } else {
            // Mostrar mensaje de error o manejar el inicio de sesión fallido
            console.log('Inicio de sesión fallido. Credenciales inválidas.');
        }
    };

    return isLoggedIn ? (
        <UserViewBuyer user={user} />
    ) : (
        <Container className="d-flex justify-content-center align-items-center flex-column vh-100">
            <h5 className="mb-3">Inicia Sesión</h5>
            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="formEmail">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        type="email"
                        placeholder="Ingresa tu email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formPassword">
                    <Form.Label>Contraseña</Form.Label>
                    <Form.Control
                        type="password"
                        placeholder="Ingresa tu contraseña"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </Form.Group>

                <Button className="mb-3" variant="dark" type="submit">
                    Iniciar Sesión
                </Button>
            </Form>
        </Container>
    );
};

export default LoginView;
